import React, { useState } from "react"
import ExpenseItem from "./ExpenseItem"
import Card from "../UI/Card"
import "./Expenses.css"
import ExpensesFilter from "./ExpensesFilter"

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState('2022')
  const filterChangeHandler = (selectedyear) => {
    setFilteredYear(selectedyear)
    // console.log('Expenses.js');
    // console.log(selectedyear);
  }
  // filter returns a brand new array 
  const filteredExpenses = props.items.filter(expense => {
    return expense.date.getFullYear().toString() === filteredYear
  })
  const arrayedItems = filteredExpenses.map((expense) =>
    <ExpenseItem
    // {/* key will help ract identify each individual item, since every item has a unique id */}
      key={expense.id}
      title={expense.title}
      amount={expense.amount}
      date={expense.date} />)
  return (
    <div>
      {/* imitates a funciton call */}
      <Card className="expenses">
        {/* controlled component */}
        <ExpensesFilter selected={filteredYear} onChangeFilter={filterChangeHandler} />
        {/* part before && is true than message will appear */}
        {arrayedItems.length === 0 && <p>No Expenses Found</p>}
        {/* if items are greater than 0 then return the list of items */}
        {arrayedItems.length > 0 && arrayedItems}
        {/* {arrayedItems} */}
      </Card>
    </div>
  )
}

export default Expenses
